import React from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';


class App extends React.Component {

    onSearchSubmit = (term) => {
        axios.get('https://api.medium.com/v1/me',
            {
                params: { query : term},
                headers: {
                    Authorization : "Client-ID  9fde62af575e4ec405d16932655bb2fe8ede514fab6266da4944e5060a1c8d81"
                }

            });
    }

    render() {
        return (

            <div className= "ui container" style={{marginTop: '15px'}}>
                <SearchBar onSubmitting={this.onSearchSubmit} />
            </div>
        );
    }
}

export default App;
